src/components/GalleryGrid.tsx.content:
import { useState } from "react";
import { galleryImages } from "@/lib/gallery-data";

interface GalleryGridProps {
  onImageClick?: (imageSrc: string) => void;
}

export default function GalleryGrid({ onImageClick }: GalleryGridProps) {
  const handleImageClick = (imageSrc: string) => {
    if (onImageClick) {
      onImageClick(imageSrc);
    }
  };

  return (
    <section id="gallery" className="py-24 bg-cream coffee-texture relative overflow-hidden" data-testid="gallery-section">
      <div className="absolute inset-0 hero-gradient opacity-40"></div>
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative">
        <div className="text-center mb-20">
          <h2 
            className="font-display font-bold text-5xl md:text-6xl bg-gradient-to-r from-warm-peach to-soft-rose bg-clip-text text-transparent mb-6 animate-fade-in drop-shadow-lg" 
            data-testid="gallery-title"
          >
            Capture the Moments
          </h2>
          <p 
            className="font-sans text-xl text-coffee-dark/80 max-w-2xl mx-auto animate-slide-up text-shadow-sm" 
            data-testid="gallery-subtitle"
          >
            Step into our world where every cup tells a story and every corner holds a memory
          </p>
        </div>

        {/* Gallery Grid */}
        <div 
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8" 
          data-testid="gallery-grid"
        >
          {galleryImages.map((image, index) => (
            <div
              key={index}
              className={`group gallery-item overflow-hidden rounded-3xl cursor-pointer transform transition-all duration-500 hover:scale-105 hover:rotate-1 hover:shadow-2xl hover:shadow-warm-peach/20 ${image.aspectRatio}`}
              onClick={() => handleImageClick(image.src)}
              data-testid={`gallery-item-${index}`}
            >
              <div className="relative w-full h-full overflow-hidden">
                <img 
                  src={image.src} 
                  alt={image.alt} 
                  className="w-full h-full object-cover transform transition-transform duration-700 group-hover:scale-110"
                  loading="lazy"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-coffee-dark/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-end p-6">
                  <p className="text-white font-display text-lg transform translate-y-4 group-hover:translate-y-0 transition-transform duration-300">
                    {image.alt}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

src/components/HeroCarousel.tsx.content:
import { useHeroSlider } from "@/hooks/use-hero-slider";

export default function HeroCarousel() {
  const { currentSlide, setCurrentSlide } = useHeroSlider();

  const slides = [
    {
      id: 0,
      image: "https://iteeha.coffee/images/vfx.png",
      title: "Coffee. Craft. Conversations.",
      subtitle: "Where every sip tells a story",
      isLogo: true,
    },
    {
      id: 1,
      image: "https://iteeha.coffee/images/urmi.jpg",
      title: "Step in, relax,",
      subtitle: "Your cozy corner awaits",
      isLogo: false,
    },
    {
      id: 2,
      image: "https://iteeha.coffee/images/e6.jpg",
      title: "And",
      subtitle: "Take a moment to breathe",
      isLogo: false,
    },
    {
      id: 3,
      image: "https://iteeha.coffee/images/e2.jpg",
      title: "let us take care of the rest.",
      subtitle: "Your perfect brew awaits",
      isLogo: false,
    },
  ];

  return (
    <section
      id="home"
      className="relative min-h-screen flex items-center coffee-texture overflow-hidden"
      data-testid="hero-carousel"
    >
      <div className="absolute inset-0 hero-gradient opacity-50"></div>

      {/* Hero Slides */}
      <div className="w-full h-screen relative overflow-hidden">
        {slides.map((slide, index) => (
          <div
            key={slide.id}
            className={`absolute inset-0 flex items-center transition-all duration-1000 ${
              currentSlide === index
                ? "opacity-100 scale-100"
                : "opacity-0 scale-105"
            }`}
            data-testid={`hero-slide-${index}`}
          >
            <div
              className="absolute inset-0 bg-cover bg-center bg-no-repeat transition-transform duration-10000 hover:scale-105"
              style={{ backgroundImage: `url('${slide.image}')` }}
            />
